import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { createPost, updatePost } from "../redux/actions";

const PostForm = ({ editingPost, setEditingPost }) => {
    const dispatch = useDispatch();
    const [post, setPost] = useState({ title: "", body: "" });

    useEffect(() => {
        if (editingPost) {
            setPost(editingPost);
        } else {
            setPost({ title: "", body: "" });
        }
    }, [editingPost]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (editingPost) {
            dispatch(updatePost(post));
        } else {
            dispatch(createPost(post));
        }
        setEditingPost(null);
        setPost({ title: "", body: "" });
    };

    return (
        <div>
            <h2>{editingPost ? "Ch·ªânh s·ª≠a b√†i vi·∫øt" : "Th√™m b√†i vi·∫øt m·ªõi"}</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Ti√™u ƒë·ªÅ"
                    value={post.title}
                    onChange={(e) => setPost({ ...post, title: e.target.value })}
                    required
                />
                <textarea
                    placeholder="N·ªôi dung"
                    value={post.body}
                    onChange={(e) => setPost({ ...post, body: e.target.value })}
                    required
                />
                <button type="submit">{editingPost ? "üíæ L∆∞u" : "‚ûï Th√™m"}</button>
                {editingPost && <button onClick={() => setEditingPost(null)}>‚ùå H·ªßy</button>}
            </form>
        </div>
    );
};

export default PostForm;
